import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

function AgentsContainer({ agents, setAgents, handleDragStart, handleAgentClick }) {
  const [newAgent, setNewAgent] = useState({ name: '', role: '', goal: '', backstory: '' });

  const addAgent = () => {
    setAgents([...agents, { ...newAgent, id: agents.length + 1 }]);
    setNewAgent({ name: '', role: '', goal: '', backstory: '' });
  };

  return (
    <div className="w-64 bg-white p-4 shadow-lg">
      <h2 className="text-xl font-bold mb-4">Agentes</h2>
      <div className="space-y-2">
        {agents.map((agent) => (
          <div
            key={agent.id}
            draggable
            onDragStart={(e) => handleDragStart(e, agent)}
            onClick={() => handleAgentClick(agent)}
            className="p-3 bg-gray-100 rounded-lg cursor-move hover:bg-gray-200 transition-colors"
          >
            {agent.name}
          </div>
        ))}
      </div>
      <div className="mt-4">
        <input type="text" placeholder="Nome" value={newAgent.name} onChange={(e) => setNewAgent({ ...newAgent, name: e.target.value })} className="w-full mb-2 p-2 border rounded" />
        <input type="text" placeholder="Role" value={newAgent.role} onChange={(e) => setNewAgent({ ...newAgent, role: e.target.value })} className="w-full mb-2 p-2 border rounded" />
        <input type="text" placeholder="Goal" value={newAgent.goal} onChange={(e) => setNewAgent({ ...newAgent, goal: e.target.value })} className="w-full mb-2 p-2 border rounded" />
        <input type="text" placeholder="Backstory" value={newAgent.backstory} onChange={(e) => setNewAgent({ ...newAgent, backstory: e.target.value })} className="w-full mb-2 p-2 border rounded" />
        <button onClick={addAgent} className="w-full bg-blue-500 text-white p-2 rounded">Adicionar Agente</button>
      </div>
    </div>
  );
}

function MainArea({ agentOrder, handleDrop, handleDragOver, handleRemoveAgent, markdownContent, selectedAgent, handleAgentChange, saveAgentChanges }) {
  return (
    <div className="flex-1 p-4">
      <div
        className={`bg-white p-4 rounded-lg shadow-lg mb-4 min-h-[200px] transition-colors ${selectedAgent ? "drop-zone-active" : ""}`}
        onDrop={handleDrop}
        onDragOver={handleDragOver}
      >
        <h3 className="text-lg font-semibold mb-4">Área de Processamento</h3>
        <div className="flex flex-wrap gap-2">
          {agentOrder.map((agent, index) => (
            <div
              key={agent.id}
              className="flex items-center bg-blue-100 px-3 py-2 rounded-full"
            >
              <span>{`${index + 1}. ${agent.name}`}</span>
              <button
                onClick={() => handleRemoveAgent(agent.id)}
                className="ml-2 text-red-500 hover:text-red-700"
              >
                ×
              </button>
            </div>
          ))}
        </div>
      </div>

      <div className="bg-white p-4 rounded-lg shadow-lg">
        <h3 className="text-lg font-semibold mb-4">Markdown Preview</h3>
        <div className="bg-gray-50 p-4 rounded-lg min-h-[300px] font-mono">
          {markdownContent || "O conteúdo markdown será exibido aqui..."}
        </div>
      </div>

      <div className="w-72 bg-white p-4 shadow-lg rounded-lg m-4">
        <h2 className="text-2xl font-bold mb-6 text-gray-800">Detalhes</h2>
        {selectedAgent ? (
          <div className="bg-gray-50 p-6 rounded-lg border border-gray-100">
            <div className="space-y-4">
              <input
                type="text"
                value={selectedAgent.name}
                onChange={(e) => handleAgentChange('name', e.target.value)}
                className="w-full p-2 border rounded mb-2"
                placeholder="Nome"
              />
              <input
                type="text"
                value={selectedAgent.role}
                onChange={(e) => handleAgentChange('role', e.target.value)}
                className="w-full p-2 border rounded mb-2"
                placeholder="Função"
              />
              <input
                type="text"
                value={selectedAgent.goal}
                onChange={(e) => handleAgentChange('goal', e.target.value)}
                className="w-full p-2 border rounded mb-2"
                placeholder="Objetivo"
              />
              <input
                type="text"
                value={selectedAgent.backstory}
                onChange={(e) => handleAgentChange('backstory', e.target.value)}
                className="w-full p-2 border rounded mb-2"
                placeholder="História"
              />
              <button
                onClick={saveAgentChanges}
                className="w-full bg-blue-500 text-white p-2 rounded"
              >
                Salvar Alterações
              </button>
            </div>
          </div>
        ) : (
          <div className="text-gray-500 text-center p-4">
            Selecione um agente para ver os detalhes
          </div>
        )}
      </div>
    </div>
  );
}

function TasksContainer({ tasks, setTasks }) {
  const [newTask, setNewTask] = useState({ description: '', expected_output: '' });

  const addTask = () => {
    setTasks([...tasks, { ...newTask, id: tasks.length + 1 }]);
    setNewTask({ description: '', expected_output: '' });
  };

  return (
    <div className="w-64 bg-white p-4 shadow-lg">
      <h2 className="text-xl font-bold mb-4">Tarefas</h2>
      <div className="space-y-2">
        {tasks.map((task) => (
          <div key={task.id} className="p-3 bg-gray-100 rounded-lg">
            {task.description}
          </div>
        ))}
      </div>
      <div className="mt-4">
        <input type="text" placeholder="Descrição" value={newTask.description} onChange={(e) => setNewTask({ ...newTask, description: e.target.value })} className="w-full mb-2 p-2 border rounded" />
        <input type="text" placeholder="Resultado Esperado" value={newTask.expected_output} onChange={(e) => setNewTask({ ...newTask, expected_output: e.target.value })} className="w-full mb-2 p-2 border rounded" />
        <button onClick={addTask} className="w-full bg-green-500 text-white p-2 rounded">Adicionar Tarefa</button>
      </div>
    </div>
  );
}

function App() {
  const [agents, setAgents] = useState([
    { id: 1, name: "Gerente", role: "Gerente", goal: "Coordenar fluxo de trabalho", backstory: "Responsável por gerenciar o processo" },
    { id: 2, name: "Pesquisador", role: "Pesquisador", goal: "Coletar informações", backstory: "Especialista em pesquisa de dados" },
    { id: 3, name: "Escritor", role: "Escritor", goal: "Criar conteúdo", backstory: "Redator especializado" },
    { id: 4, name: "Publicador", role: "Publicador", goal: "Formatar e publicar", backstory: "Especialista em markdown" }
  ]);
  const [agentOrder, setAgentOrder] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [markdownContent, setMarkdownContent] = useState("");
  const [selectedAgent, setSelectedAgent] = useState(null);

  useEffect(() => {
    const socket = io('http://localhost:6000');

    socket.on('task_results', (data) => {
      setMarkdownContent(data);
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const handleDragStart = (e, agent) => {
    e.target.classList.add("agent-drag");
    e.dataTransfer.setData("agent", JSON.stringify(agent));
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const agent = JSON.parse(e.dataTransfer.getData("agent"));
    if (!agentOrder.find((a) => a.id === agent.id)) {
      setAgentOrder([...agentOrder, agent]);
    }
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleRemoveAgent = (agentId) => {
    setAgentOrder(agentOrder.filter((agent) => agent.id !== agentId));
  };

  const handleAgentClick = (agent) => {
    setSelectedAgent(agent);
  };

  const handleAgentChange = (field, value) => {
    setSelectedAgent({ ...selectedAgent, [field]: value });
  };

  const saveAgentChanges = () => {
    const updatedAgents = agents.map((agent) =>
      agent.id === selectedAgent.id ? selectedAgent : agent
    );
    setAgents(updatedAgents);
    const socket = io('http://localhost:6000');
    socket.emit('update_agent', selectedAgent);
  };

  const triggerTask = () => {
    const socket = io('http://localhost:6000');
    socket.emit('start_tasks', { tema: 'Your Theme Here' });
  };

  return (
    <div className="flex h-screen bg-[#f5f5f5]">
      <AgentsContainer agents={agents} setAgents={setAgents} handleDragStart={handleDragStart} handleAgentClick={handleAgentClick} />
      <MainArea agentOrder={agentOrder} handleDrop={handleDrop} handleDragOver={handleDragOver} handleRemoveAgent={handleRemoveAgent} markdownContent={markdownContent} selectedAgent={selectedAgent} handleAgentChange={handleAgentChange} saveAgentChanges={saveAgentChanges} />
      <TasksContainer tasks={tasks} setTasks={setTasks} />
      <button onClick={triggerTask} className="fixed bottom-4 right-4 bg-blue-500 text-white p-2 rounded">Start Tasks</button>
    </div>
  );
}

export default App;
