import React, { useState, useEffect, useMemo } from 'react';
import io from 'socket.io-client';
import { Drawer, Button } from '@mui/material';

function AgentsContainer({ agents, setAgents, handleDragStart, handleAgentClick }) {
  const [newAgent, setNewAgent] = useState({ name: '', role: '', goal: '', backstory: '' });

  const addAgent = () => {
    setAgents([...agents, { ...newAgent, id: agents.length + 1 }]);
    setNewAgent({ name: '', role: '', goal: '', backstory: '' });
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Agentes</h2>
      <div className="space-y-3">
        {agents.map((agent) => (
          <div
            key={agent.id}
            draggable
            onDragStart={(e) => handleDragStart(e, agent)}
            onClick={() => handleAgentClick(agent)}
            className="p-4 bg-gray-50 rounded-lg cursor-move hover:bg-gray-100 transition-all transform hover:-translate-y-1 hover:shadow-md flex items-center gap-3"
          >
            <span className="font-medium">{agent.name}</span>
          </div>
        ))}
      </div>
      <div className="mt-4">
        <input type="text" placeholder="Nome" value={newAgent.name} onChange={(e) => setNewAgent({ ...newAgent, name: e.target.value })} className="w-full mb-2 p-2 border rounded" />
        <input type="text" placeholder="Role" value={newAgent.role} onChange={(e) => setNewAgent({ ...newAgent, role: e.target.value })} className="w-full mb-2 p-2 border rounded" />
        <input type="text" placeholder="Goal" value={newAgent.goal} onChange={(e) => setNewAgent({ ...newAgent, goal: e.target.value })} className="w-full mb-2 p-2 border rounded" />
        <input type="text" placeholder="Backstory" value={newAgent.backstory} onChange={(e) => setNewAgent({ ...newAgent, backstory: e.target.value })} className="w-full mb-2 p-2 border rounded" />
        <button onClick={addAgent} className="w-full bg-blue-500 text-white p-2 rounded">Adicionar Agente</button>
      </div>
    </div>
  );
}

function TasksContainer({ tasks, setTasks }) {
  const [newTask, setNewTask] = useState({ description: '', expected_output: '' });

  const addTask = () => {
    setTasks([...tasks, { ...newTask, id: tasks.length + 1 }]);
    setNewTask({ description: '', expected_output: '' });
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Tarefas</h2>
      <div className="space-y-3">
        {tasks.map((task) => (
          <div key={task.id} className="p-3 bg-gray-100 rounded-lg">
            {task.description}
          </div>
        ))}
      </div>
      <div className="mt-4">
        <input type="text" placeholder="Descrição" value={newTask.description} onChange={(e) => setNewTask({ ...newTask, description: e.target.value })} className="w-full mb-2 p-2 border rounded" />
        <input type="text" placeholder="Resultado Esperado" value={newTask.expected_output} onChange={(e) => setNewTask({ ...newTask, expected_output: e.target.value })} className="w-full mb-2 p-2 border rounded" />
        <button onClick={addTask} className="w-full bg-green-500 text-white p-2 rounded">Adicionar Tarefa</button>
      </div>
    </div>
  );
}

function MainArea({ agentOrder, handleDrop, handleDragOver, handleRemoveAgent, markdownContent, selectedAgent, handleAgentChange, saveAgentChanges, loading }) {
  return (
    <div className="flex-1 p-6 overflow-y-auto">
      <div
        className={`bg-white p-6 rounded-lg shadow-lg mb-6 min-h-[200px] transition-colors ${loading ? "drop-zone-active" : ""}`}
        onDrop={handleDrop}
        onDragOver={(e) => {
          e.preventDefault();
          e.currentTarget.classList.add("drop-zone-active");
        }}
        onDragLeave={(e) => {
          e.currentTarget.classList.remove("drop-zone-active");
        }}
      >
        <h3 className="text-xl font-semibold mb-6 text-gray-800">
          Área de Processamento
        </h3>
        <div className="flex flex-wrap gap-3">
          {agentOrder.map((agent, index) => (
            <div
              key={agent.id}
              className="flex items-center bg-blue-50 px-4 py-2 rounded-full shadow-sm hover:shadow transition-shadow"
            >
              <span className="font-medium">{`${index + 1}. ${agent.name}`}</span>
              <button
                onClick={() => handleRemoveAgent(agent.id)}
                className="ml-3 text-red-400 hover:text-red-600 w-6 h-6 flex items-center justify-center rounded-full hover:bg-red-50 transition-colors"
              >
                ×
              </button>
            </div>
          ))}
        </div>
        {loading && <div className="text-center text-blue-500 mt-4">Carregando...</div>}
      </div>

      <div className="bg-white p-6 rounded-lg shadow-lg">
        <h3 className="text-xl font-semibold mb-6 text-gray-800">
          Markdown Preview
        </h3>
        <div className="bg-gray-50 p-6 rounded-lg min-h-[300px] font-mono whitespace-pre-wrap border border-gray-100">
          {markdownContent || "O conteúdo markdown será exibido aqui..."}
        </div>
      </div>
    </div>
  );
}

function App() {
  const [agents, setAgents] = useState([
    {
      id: 1,
      name: "Gerente",
      role: "Gerente",
      goal: "Coordenar fluxo de trabalho",
      backstory: "Responsável por gerenciar o processo",
    },
    {
      id: 2,
      name: "Pesquisador",
      role: "Pesquisador",
      goal: "Coletar informações",
      backstory: "Especialista em pesquisa de dados",
    },
    {
      id: 3,
      name: "Escritor",
      role: "Escritor",
      goal: "Criar conteúdo",
      backstory: "Redator especializado",
    },
    {
      id: 4,
      name: "Publicador",
      role: "Publicador",
      goal: "Formatar e publicar",
      backstory: "Especialista em markdown",
    },
  ]);
  const [agentOrder, setAgentOrder] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [markdownContent, setMarkdownContent] = useState("");
  const [selectedAgent, setSelectedAgent] = useState(null);
  const [loading, setLoading] = useState(false);
  const [state, setState] = useState({ left: false, right: false });
  const [isDragging, setIsDragging] = useState(false);

  const socket = useMemo(() => io('http://localhost:6000'), []);

  useEffect(() => {
    socket.on('task_results', (data) => {
      console.log('Received data:', data);
      setMarkdownContent(data);
      setLoading(false);
    });

    return () => {
      socket.disconnect();
    };
  }, [socket]);

  const toggleDrawer = (anchor, open) => (event) => {
    if (
      event.type === 'keydown' &&
      (event.key === 'Tab' || event.key === 'Shift')
    ) {
      return;
    }

    setState({ ...state, [anchor]: open });
  };

  const handleDragStart = (e, agent) => {
    e.target.classList.add("agent-drag");
    setIsDragging(true);
    e.dataTransfer.setData("agent", JSON.stringify(agent));
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const dropZone = e.currentTarget;
    dropZone.classList.remove("drop-zone-active");
    setIsDragging(false);
    const agent = JSON.parse(e.dataTransfer.getData("agent"));
    if (!agentOrder.find((a) => a.id === agent.id)) {
      const newOrder = [...agentOrder, agent];
      setAgentOrder(newOrder);
    }
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleRemoveAgent = (agentId) => {
    setAgentOrder(agentOrder.filter((agent) => agent.id !== agentId));
  };

  const handleAgentClick = (agent) => {
    setSelectedAgent(agent);
  };

  const handleAgentChange = (field, value) => {
    setSelectedAgent({ ...selectedAgent, [field]: value });
  };

  const saveAgentChanges = () => {
    const updatedAgents = agents.map((agent) =>
      agent.id === selectedAgent.id ? selectedAgent : agent
    );
    setAgents(updatedAgents);
    socket.emit('update_agent', selectedAgent);
  };

  const triggerTask = () => {
    setLoading(true);
    socket.emit('start_tasks', { agents: agentOrder });
  };

  const list = (anchor) => (
    <div
      role="presentation"
      onClick={toggleDrawer(anchor, false)}
      onKeyDown={toggleDrawer(anchor, false)}
      style={{ width: '50vw' }}
    >
      {anchor === 'left' ? (
        <AgentsContainer agents={agents} setAgents={setAgents} handleDragStart={handleDragStart} handleAgentClick={handleAgentClick} />
      ) : (
        <TasksContainer tasks={tasks} setTasks={setTasks} />
      )}
    </div>
  );

  return (
    <div className="flex h-screen bg-[#f5f5f5] overflow-hidden font-roboto">
      {['left', 'right'].map((anchor) => (
        <React.Fragment key={anchor}>
          <Button onClick={toggleDrawer(anchor, true)}>{anchor}</Button>
          <Drawer
            anchor={anchor}
            open={state[anchor]}
            onClose={toggleDrawer(anchor, false)}
            PaperProps={{ style: { width: '50vw' } }}
          >
            {list(anchor)}
          </Drawer>
        </React.Fragment>
      ))}
      <MainArea agentOrder={agentOrder} handleDrop={handleDrop} handleDragOver={handleDragOver} handleRemoveAgent={handleRemoveAgent} markdownContent={markdownContent} selectedAgent={selectedAgent} handleAgentChange={handleAgentChange} saveAgentChanges={saveAgentChanges} loading={loading} />
      <button onClick={triggerTask} className="fixed bottom-4 right-4 bg-blue-500 text-white p-2 rounded">Start Tasks</button>
      <style jsx global>{`
        .agent-drag {
          transform: scale(1.05);
          opacity: 0.8;
          transition: all 0.2s ease;
        }
        .drop-zone-active {
          background-color: #e8f4ff;
          border: 2px dashed #60a5fa;
        }
      `}</style>
    </div>
  );
}

export default App;
