import React, { useState } from 'react';
import io from 'socket.io-client';

function Menu({ agents, onDragStart, handleAgentClick }) {
  return (
    <div className="w-64 bg-white p-4 shadow-lg">
      <h2 className="text-xl font-bold mb-4">Agentes</h2>
      <div className="space-y-2">
        {agents.map((agent) => (
          <div
            key={agent.id}
            draggable
            onDragStart={(e) => onDragStart(e, agent)}
            onClick={() => handleAgentClick(agent)}
            className="p-3 bg-gray-100 rounded-lg cursor-move hover:bg-gray-200 transition-colors"
          >
            {agent.name}
          </div>
        ))}
      </div>
    </div>
  );
}

function MainArea({ agentOrder, handleDrop, handleDragOver, handleRemoveAgent, markdownContent, setPrompt, generateContent }) {
  return (
    <div className="flex-1 p-4">
      <div
        className="bg-white p-4 rounded-lg shadow-lg mb-4 min-h-[200px]"
        onDrop={handleDrop}
        onDragOver={handleDragOver}
      >
        <h3 className="text-lg font-semibold mb-4">Área de Processamento</h3>
        <div className="flex flex-wrap gap-2">
          {agentOrder.map((agent, index) => (
            <div
              key={agent.id}
              className="flex items-center bg-blue-100 px-3 py-2 rounded-full"
            >
              <span>{`${index + 1}. ${agent.name}`}</span>
              <button
                onClick={() => handleRemoveAgent(agent.id)}
                className="ml-2 text-red-500 hover:text-red-700"
              >
                ×
              </button>
            </div>
          ))}
        </div>
      </div>

      <div className="bg-white p-4 rounded-lg shadow-lg">
        <h3 className="text-lg font-semibold mb-4">Markdown Preview</h3>
        <div className="bg-gray-50 p-4 rounded-lg min-h-[300px] font-mono">
          {markdownContent || "O conteúdo markdown será exibido aqui..."}
        </div>
      </div>
      <input type="text" placeholder="Digite o prompt" onChange={(e) => setPrompt(e.target.value)} />
      <button onClick={generateContent}>Gerar Conteúdo</button>
    </div>
  );
}

function Details({ selectedAgent }) {
  if (!selectedAgent) return <div className="w-64 bg-white p-4 shadow-lg"><h2 className="text-xl font-bold mb-4">Detalhes</h2><p>Nenhum agente selecionado</p></div>;

  return (
    <div className="w-64 bg-white p-4 shadow-lg">
      <h2 className="text-xl font-bold mb-4">Detalhes</h2>
      <div className="bg-gray-50 p-4 rounded-lg">
        <h3 className="font-semibold mb-2">Agente</h3>
        <div className="space-y-2">
          <p className="text-sm">
            <span className="text-gray-500">Role:</span>{" "}
            {selectedAgent.role}
          </p>
          <p className="text-sm">
            <span className="text-gray-500">Goal:</span>{" "}
            {selectedAgent.goal}
          </p>
          <p className="text-sm">
            <span className="text-gray-500">Backstory:</span>{" "}
            {selectedAgent.backstory}
          </p>
        </div>
      </div>
    </div>
  );
}

function App() {
  const [selectedAgent, setSelectedAgent] = useState(null);
  const [agents] = useState([
    { id: 1, name: "Gerente", role: "Gerente", goal: "Coordenar fluxo de trabalho", backstory: "Responsável por gerenciar o processo" },
    { id: 2, name: "Pesquisador", role: "Pesquisador", goal: "Coletar informações", backstory: "Especialista em pesquisa de dados" },
    { id: 3, name: "Escritor", role: "Escritor", goal: "Criar conteúdo", backstory: "Redator especializado" },
    { id: 4, name: "Publicador", role: "Publicador", goal: "Formatar e publicar", backstory: "Especialista em markdown" }
  ]);
  const [agentOrder, setAgentOrder] = useState([]);
  const [markdownContent, setMarkdownContent] = useState("");
  const [prompt, setPrompt] = useState("");

  const handleDragStart = (e, agent) => {
    e.dataTransfer.setData("agent", JSON.stringify(agent));
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const agent = JSON.parse(e.dataTransfer.getData("agent"));
    if (!agentOrder.find((a) => a.id === agent.id)) {
      setAgentOrder([...agentOrder, agent]);
    }
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleRemoveAgent = (agentId) => {
    setAgentOrder(agentOrder.filter((agent) => agent.id !== agentId));
  };

  const handleAgentClick = (agent) => {
    setSelectedAgent(agent);
  };

  const generateContent = () => {
    const socket = io('http://localhost:6000');
    socket.emit('start_tasks', { tema: prompt });
    socket.on('task_results', (data) => {
      setMarkdownContent(data);
    });
  };

  return (
    <div className="flex h-screen bg-[#f5f5f5]">
      <Menu agents={agents} onDragStart={handleDragStart} handleAgentClick={handleAgentClick} />
      <MainArea agentOrder={agentOrder} handleDrop={handleDrop} handleDragOver={handleDragOver} handleRemoveAgent={handleRemoveAgent} markdownContent={markdownContent} setPrompt={setPrompt} generateContent={generateContent} />
      <Details selectedAgent={selectedAgent} />
    </div>
  );
}

export default App;
