import { useState, useEffect, useCallback } from "react";
import {
  AppBar,
  Toolbar,
  Typography,
  Container,
  Grid,
  TextField,
  Button,
  Box,
  Paper,
  CircularProgress,
  Alert,
} from "@mui/material";
import ReactMarkdown from "react-markdown";
import AgentModal from './components/AgentModal';
import AgentList from './components/AgentList';

const useWebSocket = () => {
  const [ws, setWs] = useState(null);
  const [connectionStatus, setConnectionStatus] = useState('disconnected');

  useEffect(() => {
    const websocket = new WebSocket('ws://localhost:9400');

    websocket.onopen = () => {
      console.log('Connected to WebSocket server');
      setConnectionStatus('connected');
    };

    websocket.onclose = () => {
      console.log('Disconnected from WebSocket server');
      setConnectionStatus('disconnected');
    };

    websocket.onerror = (error) => {
      console.error('WebSocket error:', error);
      setConnectionStatus('error');
    };

    setWs(websocket);

    return () => {
      if (websocket.readyState === WebSocket.OPEN) {
        websocket.close();
      }
    };
  }, []);

  return { ws, connectionStatus };
};

function App() {
  const [agents, setAgents] = useState([
    {
      id: 1,
      name: "Pesquisador",
      role: "Pesquisador",
      goal: "Pesquisar informações relevantes sobre o assunto",
      backstory: "Você é um pesquisador experiente e está sempre em busca de informações relevantes.",
      expected_output: "Um relatório com parágrafos contendo Introdução, Desenvolvimento, e Conclusão",
    },
    {
      id: 2,
      name: "Redator",
      role: "Redator",
      goal: "Escrever um artigo informativo sobre o assunto",
      backstory: "Você é um redator experiente e está sempre buscando escrita limpa e facil de entendimento.",
      expected_output: "Arquivo markdown bem escrito e objetivo de forma didática",
    },
  ]);

  const [selectedAgents, setSelectedAgents] = useState([]);
  const [loading, setLoading] = useState(false);
  const [prompt, setPrompt] = useState("");
  const [markdownResult, setMarkdownResult] = useState("");
  const [modalOpen, setModalOpen] = useState(false);
  const [editingAgent, setEditingAgent] = useState(null);
  const { ws, connectionStatus } = useWebSocket();

  const handleMessage = useCallback((event) => {
    try {
      const data = JSON.parse(event.data);
      console.log('Received message:', data);

      if (data.type === 'task_results') {
        setLoading(false);
        if (data.status === 'success') {
          setMarkdownResult(data.markdown_result || '');
        } else {
          console.error('Task execution failed:', data.message);
          alert('Erro ao executar as tarefas. Por favor, tente novamente.');
        }
      }
    } catch (error) {
      console.error('Error processing message:', error);
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    if (ws) {
      ws.onmessage = handleMessage;
    }
  }, [ws, handleMessage]);

  const handleStartTasks = () => {
    if (!ws || ws.readyState !== WebSocket.OPEN) {
      alert('Erro de conexão com o servidor. Por favor, recarregue a página.');
      return;
    }

    if (!prompt.trim()) {
      alert('Por favor, insira um tema antes de iniciar as tarefas.');
      return;
    }

    if (selectedAgents.length === 0) {
      alert('Por favor, selecione pelo menos um agente.');
      return;
    }

    const tasks = selectedAgents.map((agent, index) => ({
      description: `${agent.role} - ${prompt}`,
      agentId: agent.id,
      expected_output: agent.expected_output,
      attempts: 2
    }));

    setLoading(true);
    setMarkdownResult('');

    ws.send(JSON.stringify({
      type: 'start_tasks',
      data: {
        tema: prompt,
        agents: selectedAgents,
        tasks: tasks
      }
    }));
  };

  const handleSaveAgent = (agentData) => {
    if (editingAgent) {
      setAgents(agents.map(agent => 
        agent.id === editingAgent.id ? { ...agentData, id: agent.id } : agent
      ));
    } else {
      const newAgent = {
        ...agentData,
        id: agents.length + 1,
      };
      setAgents([...agents, newAgent]);
    }
    setModalOpen(false);
    setEditingAgent(null);
  };

  const handleEditAgent = (agent) => {
    setEditingAgent(agent);
    setModalOpen(true);
  };

  const handleDeleteAgent = (agentId) => {
    setAgents(agents.filter(agent => agent.id !== agentId));
    setSelectedAgents(selectedAgents.filter(agent => agent.id !== agentId));
  };

  const handleSelectAgent = (agent) => {
    if (!selectedAgents.find(a => a.id === agent.id)) {
      setSelectedAgents([...selectedAgents, agent]);
    }
  };

  return (
    <Box sx={{ flexGrow: 1 }}>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            AI Agents Dashboard
          </Typography>
          <Typography variant="body2" sx={{ mr: 2 }}>
            Status: {connectionStatus}
          </Typography>
        </Toolbar>
      </AppBar>

      <Container maxWidth="lg" sx={{ mt: 4 }}>
        <Grid container spacing={3}>
          <Grid item xs={12}>
            <Box sx={{ mb: 2 }}>
              <Button
                variant="contained"
                color="primary"
                onClick={() => setModalOpen(true)}
                sx={{ mr: 2 }}
              >
                Adicionar Novo Agente
              </Button>
            </Box>
            <AgentList
              agents={agents}
              onSelectAgent={handleSelectAgent}
              onEditAgent={handleEditAgent}
              onDeleteAgent={handleDeleteAgent}
            />
          </Grid>

          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Tema para os Agentes"
              value={prompt}
              onChange={(e) => setPrompt(e.target.value)}
              disabled={loading}
              margin="normal"
            />
          </Grid>

          <Grid item xs={12}>
            <Box sx={{ mb: 2 }}>
              <Typography variant="h6" gutterBottom>
                Agentes Selecionados:
              </Typography>
              {selectedAgents.map((agent) => (
                <Typography key={agent.id} variant="body1">
                  {agent.name} ({agent.role})
                </Typography>
              ))}
            </Box>
          </Grid>

          <Grid item xs={12}>
            <Button
              variant="contained"
              color="primary"
              onClick={handleStartTasks}
              disabled={loading || !prompt.trim() || selectedAgents.length === 0}
              fullWidth
            >
              {loading ? <CircularProgress size={24} color="inherit" /> : 'Iniciar Tarefas'}
            </Button>
          </Grid>

          {markdownResult && (
            <Grid item xs={12}>
              <Paper sx={{ p: 2, mt: 2 }}>
                <ReactMarkdown>{markdownResult}</ReactMarkdown>
              </Paper>
            </Grid>
          )}
        </Grid>

        <AgentModal
          open={modalOpen}
          handleClose={() => {
            setModalOpen(false);
            setEditingAgent(null);
          }}
          handleSave={handleSaveAgent}
          editingAgent={editingAgent}
        />
      </Container>
    </Box>
  );
}

export default App;
