import React, { useState, useEffect } from "react";
import {
  AppBar,
  Toolbar,
  Typography,
  Container,
  Grid,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Paper,
  Button,
  Divider,
} from "@mui/material";
import ReactMarkdown from "react-markdown";

function App() {
  const [agents, setAgents] = useState([]);
  const [agentOrder, setAgentOrder] = useState([]);
  const [ws, setWs] = useState(null);
  const [loading, setLoading] = useState(false);
  const [prompt, setPrompt] = useState("");
  const [markdownResult, setMarkdownResult] = useState("");
  const [isConfigValid, setIsConfigValid] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState('disconnected');
  const [open, setOpen] = useState(false);
  const [newAgentName, setNewAgentName] = useState("");

  useEffect(() => {
    const websocket = new WebSocket('ws://localhost:4000');

    websocket.onopen = () => {
      console.log('Connected to WebSocket server');
      setConnectionStatus('connected');
    };

    websocket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      console.log('Message from server ', data);
      if (data.type === 'task_results') {
        setMarkdownResult(data.results.join("\n---\n"));
        setLoading(false);
      }
    };

    websocket.onclose = () => {
      console.log('Disconnected from WebSocket server');
      setConnectionStatus('disconnected');
    };

    websocket.onerror = (error) => {
      console.error('WebSocket error: ', error);
      setConnectionStatus('error');
    };

    setWs(websocket);

    return () => {
      websocket.close();
    };
  }, []);

  useEffect(() => {
    const isValid = agentOrder.length > 0 && prompt.trim() !== '';
    setIsConfigValid(isValid);
  }, [agentOrder, prompt]);

  const handleStartTasks = () => {
    if (!ws || ws.readyState !== WebSocket.OPEN) {
      console.error('WebSocket not connected');
      alert('Erro de conexão com o servidor. Por favor, recarregue a página.');
      return;
    }

    const selectedAgents = agentOrder.map(id => {
      const agent = agents.find(a => a.id === id);
      return {
        id: agent.id,
        name: agent.name,
        role: agent.role,
        goal: agent.goal,
        backstory: agent.backstory,
        tasks: agent.tasks,
        expected_output: agent.expected_output
      };
    });

    const data = {
      tema: prompt,
      agents: selectedAgents,
      tasks: [
        {
          description: `Pesquisar sobre ${prompt} com as fontes mais recentes e confiáveis`,
          agentId: 1,
          expected_output: 'Um relatório com parágrafos contendo Introdução, Desenvolvimento, e Conclusão',
          attempts: 2
        },
        {
          description: `Escrever um artigo em formato markdown sobre ${prompt} com base na pesquisa realizada`,
          agentId: 2,
          expected_output: 'Arquivo markdown bem escrito e objetivo de forma didática',
          attempts: 2
        }
      ]
    };

    setLoading(true);
    ws.send(JSON.stringify({
      type: 'start_tasks',
      data
    }));
  };

  const handleSelectAgent = (agent) => {
    if (!agentOrder.includes(agent.id)) {
      const newOrder = [...agentOrder, agent.id];
      setAgentOrder(newOrder);
    }
  };

  const handleAddAgent = () => {
    const newAgent = {
      id: agents.length + 1,
      name: newAgentName,
      role: 'Novo Agente',
      goal: 'Definir objetivo',
      backstory: 'Definir história',
      tasks: [],
      expected_output: 'Definir saída esperada'
    };
    setAgents([...agents, newAgent]);
    setOpen(false);
    setNewAgentName("");
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <Container>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" style={{ flexGrow: 1 }}>
            Gerador de Agentes IA
          </Typography>
        </Toolbar>
      </AppBar>
      <Grid container spacing={3}>
        <Grid item xs={12} md={6}>
          <Paper elevation={3} className="p-6" style={{ minHeight: '80vh' }}>
            <Typography variant="h6" gutterBottom style={{ color: '#6a1b9a' }}>
              Configuração dos Agentes
            </Typography>
            <Divider style={{ marginBottom: '10px' }} />
            <div>
              {agents.map(agent => (
                <div key={agent.id}>
                  <Typography variant="body1">{agent.name}</Typography>
                  <Button onClick={() => handleSelectAgent(agent)}>Selecionar</Button>
                </div>
              ))}
              <Button onClick={() => setOpen(true)}>Adicionar Novo Agente</Button>
              <Dialog open={open} onClose={handleClose}>
                <DialogTitle>Adicionar Novo Agente</DialogTitle>
                <DialogContent>
                  <TextField
                    label="Nome do Agente"
                    value={newAgentName}
                    onChange={(e) => setNewAgentName(e.target.value)}
                    fullWidth
                    margin="dense"
                  />
                </DialogContent>
                <DialogActions>
                  <Button onClick={handleClose}>Cancelar</Button>
                  <Button onClick={handleAddAgent}>Adicionar</Button>
                </DialogActions>
              </Dialog>
            </div>
          </Paper>
        </Grid>
        <Grid item xs={12} md={6}>
          <Paper elevation={3} className="p-6" style={{ minHeight: '80vh' }}>
            <Typography variant="h6" gutterBottom style={{ color: '#6a1b9a' }}>
              Tarefas dos Agentes
            </Typography>
            <Divider style={{ marginBottom: '10px' }} />
            <TextField
              label="Tema"
              value={prompt}
              onChange={(e) => setPrompt(e.target.value)}
              fullWidth
              margin="normal"
              multiline
              rows={4}
            />
            <ReactMarkdown>{markdownResult}</ReactMarkdown>
            <Button
              variant="contained"
              color={isConfigValid ? "success" : "error"}
              onClick={handleStartTasks}
              disabled={loading || !isConfigValid}
              style={{ marginTop: '20px', marginBottom: '20px' }}
              fullWidth
            >
              {loading ? (
                <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                  <span>Processando</span>
                  <div className="loading-dots">
                    <span>.</span><span>.</span><span>.</span>
                  </div>
                </div>
              ) : (
                isConfigValid ? 'Iniciar Tarefas de agentes IA' : 'Complete a Configuração'
              )}
            </Button>
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
}

export default App;
