import React, { useState, useEffect, useMemo } from 'react';
import io from 'socket.io-client';
import { Drawer, Button, AppBar, Toolbar, Typography, Container, Paper, Grid, TextField } from '@mui/material';

function App() {
  const [agents, setAgents] = useState([
    {
      id: 1,
      name: "Gerente",
      role: "Gerente",
      goal: "Coordenar fluxo de trabalho",
      backstory: "Responsável por gerenciar o processo",
    },
    {
      id: 2,
      name: "Pesquisador",
      role: "Pesquisador",
      goal: "Coletar informações",
      backstory: "Especialista em pesquisa de dados",
    },
    {
      id: 3,
      name: "Escritor",
      role: "Escritor",
      goal: "Criar conteúdo",
      backstory: "Redator especializado",
    },
    {
      id: 4,
      name: "Publicador",
      role: "Publicador",
      goal: "Formatar e publicar",
      backstory: "Especialista em markdown",
    },
  ]);
  const [agentOrder, setAgentOrder] = useState([]);
  const [markdownContent, setMarkdownContent] = useState("");
  const [selectedAgent, setSelectedAgent] = useState(null);
  const [loading, setLoading] = useState(false);
  const [state, setState] = useState({ left: false, right: false });
  const [isDragging, setIsDragging] = useState(false);

  const socket = useMemo(() => io('http://localhost:6000'), []);

  useEffect(() => {
    socket.on('task_results', (data) => {
      console.log('Received data:', data);
      setMarkdownContent(data);
      setLoading(false);
    });

    return () => {
      socket.disconnect();
    };
  }, [socket]);

  const toggleDrawer = (anchor, open) => (event) => {
    if (
      event.type === 'keydown' &&
      (event.key === 'Tab' || event.key === 'Shift')
    ) {
      return;
    }

    setState({ ...state, [anchor]: open });
  };

  const handleDragStart = (e, agent) => {
    e.target.classList.add("agent-drag");
    setIsDragging(true);
    e.dataTransfer.setData("agent", JSON.stringify(agent));
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const dropZone = e.currentTarget;
    dropZone.classList.remove("drop-zone-active");
    setIsDragging(false);
    const agent = JSON.parse(e.dataTransfer.getData("agent"));
    if (!agentOrder.find((a) => a.id === agent.id)) {
      const newOrder = [...agentOrder, agent];
      setAgentOrder(newOrder);
    }
  };

  const handleRemoveAgent = (agentId) => {
    setAgentOrder(agentOrder.filter((agent) => agent.id !== agentId));
  };

  const handleAgentClick = (agent) => {
    setSelectedAgent(agent);
    toggleDrawer('right', true)();
  };

  const handleAgentChange = (field, value) => {
    setSelectedAgent({ ...selectedAgent, [field]: value });
  };

  const saveAgentChanges = () => {
    const updatedAgents = agents.map((agent) =>
      agent.id === selectedAgent.id ? selectedAgent : agent
    );
    setAgents(updatedAgents);
    socket.emit('update_agent', selectedAgent);
  };

  const triggerTask = () => {
    setLoading(true);
    socket.emit('start_tasks', { tema: 'Your Theme Here' });
  };

  const list = (anchor) => (
    <div
      role="presentation"
      onClick={toggleDrawer(anchor, false)}
      onKeyDown={toggleDrawer(anchor, false)}
      style={{ width: '50vw' }}
    >
      {anchor === 'left' ? (
        <div className="p-4">
          <Typography variant="h6" gutterBottom>Agentes</Typography>
          <div className="space-y-3">
            {agents.map((agent) => (
              <div
                key={agent.id}
                draggable
                onDragStart={(e) => handleDragStart(e, agent)}
                onClick={() => handleAgentClick(agent)}
                className="p-4 bg-gray-50 rounded-lg cursor-move hover:bg-gray-100 transition-all transform hover:-translate-y-1 hover:shadow-md flex items-center gap-3"
              >
                <Typography variant="body1">{agent.name}</Typography>
              </div>
            ))}
          </div>
          <Button variant="contained" color="primary" fullWidth onClick={() => setAgents([...agents, { id: agents.length + 1, name: '', role: '', goal: '', backstory: '' }])}>Adicionar Novo Agente</Button>
        </div>
      ) : (
        <div className="p-4">
          {selectedAgent && (
            <div>
              <Typography variant="h6" gutterBottom>Editar Agente</Typography>
              <TextField
                label="Nome"
                value={selectedAgent.name}
                onChange={(e) => handleAgentChange('name', e.target.value)}
                fullWidth
                margin="normal"
              />
              <TextField
                label="Função"
                value={selectedAgent.role}
                onChange={(e) => handleAgentChange('role', e.target.value)}
                fullWidth
                margin="normal"
              />
              <TextField
                label="Objetivo"
                value={selectedAgent.goal}
                onChange={(e) => handleAgentChange('goal', e.target.value)}
                fullWidth
                margin="normal"
              />
              <TextField
                label="História"
                value={selectedAgent.backstory}
                onChange={(e) => handleAgentChange('backstory', e.target.value)}
                fullWidth
                margin="normal"
              />
              <Button variant="contained" color="primary" onClick={saveAgentChanges} fullWidth>
                Salvar Alterações
              </Button>
            </div>
          )}
        </div>
      )}
    </div>
  );

  return (
    <div className="flex h-screen bg-[#f5f5f5] overflow-hidden font-roboto">
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" style={{ flexGrow: 1 }}>
            Gerador de Agentes IA
          </Typography>
          <Button color="inherit" onClick={triggerTask}>Start Tasks</Button>
        </Toolbar>
      </AppBar>
      {['left', 'right'].map((anchor) => (
        <React.Fragment key={anchor}>
          <Drawer
            anchor={anchor}
            open={state[anchor]}
            onClose={toggleDrawer(anchor, false)}
            PaperProps={{ style: { width: '50vw' } }}
          >
            {list(anchor)}
          </Drawer>
        </React.Fragment>
      ))}
      <Container style={{ marginTop: '20px' }}>
        <Grid container spacing={3}>
          <Grid item xs={12} md={4}>
            <Paper elevation={3} className="p-6">
              <Typography variant="h6" gutterBottom>
                Agentes
              </Typography>
              <div className="space-y-3">
                {agents.map((agent) => (
                  <div
                    key={agent.id}
                    draggable
                    onDragStart={(e) => handleDragStart(e, agent)}
                    onClick={() => handleAgentClick(agent)}
                    className="p-4 bg-gray-50 rounded-lg cursor-move hover:bg-gray-100 transition-all transform hover:-translate-y-1 hover:shadow-md flex items-center gap-3"
                  >
                    <Typography variant="body1">{agent.name}</Typography>
                  </div>
                ))}
              </div>
              <Button variant="contained" color="primary" fullWidth onClick={() => setAgents([...agents, { id: agents.length + 1, name: '', role: '', goal: '', backstory: '' }])}>Adicionar Novo Agente</Button>
            </Paper>
          </Grid>
          <Grid item xs={12} md={4}>
            <Paper elevation={3} className="p-6">
              <Typography variant="h6" gutterBottom>
                Área de Processamento
              </Typography>
              <div
                className={`bg-white p-6 rounded-lg shadow-lg mb-6 min-h-[200px] transition-colors ${isDragging ? "drop-zone-active" : ""}`}
                onDrop={handleDrop}
                onDragOver={(e) => {
                  e.preventDefault();
                  e.currentTarget.classList.add("drop-zone-active");
                }}
                onDragLeave={(e) => {
                  e.currentTarget.classList.remove("drop-zone-active");
                }}
              >
                <div className="flex flex-wrap gap-3">
                  {agentOrder.map((agent, index) => (
                    <div
                      key={agent.id}
                      className="flex items-center bg-blue-50 px-4 py-2 rounded-full shadow-sm hover:shadow transition-shadow"
                    >
                      <Typography variant="body2">{`${index + 1}. ${agent.name}`}</Typography>
                      <Button
                        onClick={() => handleRemoveAgent(agent.id)}
                        color="secondary"
                        size="small"
                        style={{ marginLeft: '10px' }}
                      >
                        ×
                      </Button>
                    </div>
                  ))}
                </div>
                {loading && <Typography variant="body2" color="primary" align="center" style={{ marginTop: '10px' }}>Carregando...</Typography>}
              </div>
            </Paper>
          </Grid>
          <Grid item xs={12} md={4}>
            <Paper elevation={3} className="p-6">
              <Typography variant="h6" gutterBottom>
                Markdown Preview
              </Typography>
              <div className="bg-gray-50 p-6 rounded-lg min-h-[300px] font-mono whitespace-pre-wrap border border-gray-100">
                {markdownContent || "O conteúdo markdown será exibido aqui..."}
              </div>
            </Paper>
          </Grid>
        </Grid>
      </Container>
      <style jsx global>{`
        .agent-drag {
          transform: scale(1.05);
          opacity: 0.8;
          transition: all 0.2s ease;
        }
        .drop-zone-active {
          background-color: #e8f4ff;
          border: 2px dashed #60a5fa;
        }
      `}</style>
    </div>
  );
}

export default App;
